-- SQLite-specific seeder for blog_posts table
INSERT OR IGNORE INTO blog_posts (id, title, slug, content, excerpt, featured_image, category_id, author_id, status, published_at, created_at, updated_at) VALUES
(1, 'A Importância da Certificação FSC na Madeira', 'importancia-certificacao-fsc-madeira', '<p>A certificação FSC (Forest Stewardship Council) é fundamental para garantir que a madeira utilizada em seus projetos seja proveniente de florestas manejadas de forma responsável...</p><p>Neste artigo, vamos explorar os benefícios da certificação FSC e como ela impacta positivamente o meio ambiente e a sociedade.</p>', 'Entenda por que a certificação FSC é essencial para um consumo responsável de madeira.', '/images/blog/fsc-certification.jpg', 4, 1, 'published', datetime('now', '-30 days'), datetime('now', '-30 days'), datetime('now', '-30 days')),
(2, 'Tendências em Construção Sustentável com Madeira', 'tendencias-construcao-sustentavel-madeira', '<p>A construção civil está passando por uma revolução verde, e a madeira está no centro dessa transformação...</p><p>Descubra as principais tendências em construção sustentável e como a madeira certificada pode ser a solução para projetos mais ecológicos.</p>', 'Explore as últimas tendências em construção sustentável utilizando madeira certificada.', '/images/blog/sustainable-construction.jpg', 2, 3, 'published', datetime('now', '-25 days'), datetime('now', '-25 days'), datetime('now', '-25 days')),
(3, 'Técnicas Avançadas de Marcenaria: Trabalhando com Madeiras Exóticas', 'tecnicas-avancadas-marcenaria-madeiras-exoticas', '<p>As madeiras exóticas oferecem possibilidades únicas para projetos de marcenaria, mas requerem técnicas específicas...</p><p>Aprenda as melhores práticas para trabalhar com madeiras exóticas e criar peças únicas e duradouras.</p>', 'Domine as técnicas para trabalhar com madeiras exóticas em seus projetos de marcenaria.', '/images/blog/exotic-wood-carpentry.jpg', 3, 1, 'published', datetime('now', '-20 days'), datetime('now', '-20 days'), datetime('now', '-20 days')),
(4, 'O Futuro da Indústria Madeireira: Tecnologia e Inovação', 'futuro-industria-madeireira-tecnologia-inovacao', '<p>A indústria madeireira está se transformando rapidamente com o advento de novas tecnologias...</p><p>Desde o uso de drones para monitoramento florestal até técnicas avançadas de processamento, veja como a tecnologia está revolucionando o setor.</p>', 'Descubra como a tecnologia está transformando a indústria madeireira.', '/images/blog/wood-industry-technology.jpg', 6, 3, 'published', datetime('now', '-15 days'), datetime('now', '-15 days'), datetime('now', '-15 days')),
(5, 'Análise de Mercado: Preços da Madeira em 2024', 'analise-mercado-precos-madeira-2024', '<p>O mercado madeireiro passou por significativas mudanças em 2024...</p><p>Analisamos as principais tendências de preços, fatores que influenciam o mercado e projeções para os próximos meses.</p>', 'Análise completa dos preços e tendências do mercado madeireiro em 2024.', '/images/blog/wood-market-analysis.jpg', 5, 1, 'draft', NULL, datetime('now', '-10 days'), datetime('now', '-5 days'));